import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

let defaultTextColor = '#fff';
let defaultStyle = {
    color: defaultTextColor
};

let fakeServerData = {
    user: {
        name: 'Brian',
        playlists: [
            {
                name:'My favorites',
                songs:[{name:'Beat It',duration: 1345},{name:'Test',duration:15}]
            },
            {
                name:'My favorites2',
                songs:[{name:'Beat It',duration: 1345},{name:'Test',duration:15}]
            },
            {
                name:'My favorites3',
                songs:[{name:'Beat It',duration: 1345},{name:'Test',duration:15}]
            },
            {
                name:'My favorites4',
                songs:[{name:'Beat It',duration: 1345},{name:'Test',duration:15}]
            }
        ]
    }
};

class PlaylistCounter extends Component {
    render() {
        return (
            <div style={{width: "40%", display: 'inline-block'}}>
                <h2 style={defaultStyle}>{this.props.playlists.length} Playlists</h2>
            </div>
        );
    }
}

class HoursCounter extends Component {
    render() {
        let allSongs = this.props.playlists.reduce((songs,eachPlaylist) => {
            return songs.concat(eachPlaylist.songs)
        } ,[]);
        let totalDuration = allSongs.reduce((sum, eachSong) => { 
            return sum + eachSong.duration
        }, 0)
        return (
            <div style={{width: "40%", display: 'inline-block'}}>
                <h2 style={defaultStyle}>{Math.round(totalDuration/60)} Hours</h2>
            </div>
        );
    }
}

class Filter extends Component {
    render() {
        return (
            <div>
                <img/>
                Playlist<input type="text"/>
            </div>
        );
    }
}

class Playlist extends Component {
    render() {
        return (
            <div style={{...defaultStyle,width: "25%",display: 'inline-block'}}>
                <img/>
                <h3>Playlist Name</h3>
                <ul><li>Song 1</li>
                    <li>Song 2</li>
                </ul>
            </div>
        );
    }
}


class App extends Component {
    constructor() {
        super();
        this.state = {serverData: {}}
    }
    componentDidMount() {
        setTimeout(() => {
            this.setState({serverData: fakeServerData});
        }, 1000);
    }
    render() {
        return (
            <div className="App">
                {this.state.serverData.user ? 
                  <div>
                    <h1 style={{...defaultStyle, 'font-size': '54px'}}>Music Switcher</h1>
                    <h2 style={{...defaultStyle}}>{this.state.serverData.user.name}</h2>
                    <PlaylistCounter playlists={this.state.serverData.user.playlists}/> 
                    <HoursCounter playlists={this.state.serverData.user.playlists}/>
                    <Filter/>
                    {this.state.serverData.user.playlists.map(playlist =>
                       <Playlist/> 
                    )}
                    <Playlist/>
                    <Playlist/>
                    <Playlist/>
                    <Playlist/>
                  </div> : <h1>Loading...</h1>
                }
            </div>
        );
    }
}
export default App;
